package provider

import (
	"fmt"
	"testing"

	"github.com/hashicorp/terraform-plugin-testing/helper/resource"
)

func TestAccTrustframeworkKeySetCertificateDataSource(t *testing.T) {
	resource.Test(t, resource.TestCase{
		PreCheck:                 func() { testAccPreCheck(t) },
		ProtoV6ProviderFactories: testAccProtoV6ProviderFactories,
		Steps: []resource.TestStep{
			{
				Config: testAccTrustframeworkKeySetCertificateDataSource("TestCert"),
				Check: resource.ComposeAggregateTestCheckFunc(
					resource.TestCheckResourceAttr("data.azureadb2c_trustframework_keyset_certificate.test", "keyset_id", "B2C_1A_TestCert"),
					resource.TestCheckResourceAttr("data.azureadb2c_trustframework_keyset_certificate.test", "data.kty", "RSA"),
				),
			},
		},
	})
}

func testAccTrustframeworkKeySetCertificateDataSource(name string) string {
	return fmt.Sprintf(`
resource "azureadb2c_trustframework_keyset" "test" {
  name = %[1]q
}

resource "azureadb2c_trustframework_keyset_certificate" "test" {
  keyset_id = azureadb2c_trustframework_keyset.test.id
  certificate = ""	
  password = "Trigger.07"	
}

data "azureadb2c_trustframework_keyset_certificate" "test" {
  keyset_id = azureadb2c_trustframework_keyset_certificate.test.keyset_id
}
`, name)
}
